import React from "react";
import { FaSync } from "react-icons/fa";
import './ProjectsPage.css';

const ProjectsPage = () => {
    const project1 = `${process.env.PUBLIC_URL}/UMateLogo.jpeg`;
    const project2 = `${process.env.PUBLIC_URL}/TravelPlannerLogo.jpeg`;
    const project3 = `${process.env.PUBLIC_URL}/WeatherApp.png`;

  const projects = [
    {
      title: "UMate - Bumble for Roommates",
      description: "An app designed to simplify the process of finding compatible roommates within UMass by utilizing React, Flask, and MongoDB. By using a recommendation model, UMate suggests the top matches based on compatibility factors like the duration of stay, lifestyle, budget, and more.",
      img: project1,
      link: "https://github.com/devyanigoil/UMate",
      background: "cover",
    },
    {
      title: "Travel Planner",
      description: "This is an AI-powered platform designed to enhance and simplify the travel planning experience. By leveraging the capabilities of Llama-3.2-3B-Instruct, the application curates personalized itineraries, suggests destinations based on user moods, provides a comprehensive packing checklist, and more.",
      img: project2,
      link: "https://github.com/devyanigoil/travel-planner",
      background: "auto",
    },
    {
      title: "Weather App",
      description: "Project 3 is a mobile app that helps track daily fitness activities. Built with React Native and integrated with health data APIs.",
      img: "https://via.placeholder.com/300x200/FFD700/FFFFFF?text=Project+3",
      link: project3,
      background: "auto",
    },
    {
      title: "Project 4",
      description: "A real-time chat application developed with Node.js, Express, and Socket.io that allows users to chat instantly.",
      img: "https://via.placeholder.com/300x200/8A2BE2/FFFFFF?text=Project+4",
    },
    {
      title: "Project 5",
      description: "A weather forecasting web app that provides real-time weather information for any city, built with React and OpenWeather API.",
      img: "https://via.placeholder.com/300x200/DC143C/FFFFFF?text=Project+5",
    },
    {
      title: "Project 6",
      description: "Project 6 is an e-commerce website built with React and Redux for state management, featuring a full shopping cart experience.",
      img: "https://via.placeholder.com/300x200/20B2AA/FFFFFF?text=Project+6",
    },
  ];

  return (
    <div className="projects-section">
      <h1>PROJECTS</h1>
      <div className="projects-container">
        {projects.map((project, index) => (
          <div key={index} className="project-card" style={{ backgroundImage: `url(${project.img})` , backgroundSize: project.background}}>
            <div className="overlay">
              <h2>{project.title}</h2>
              <p>{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer">
              <p>PROJECT CODE</p> </a>
            </div>
          </div>
        ))}
      </div>
      <div className="load-more">
        <a href="https://github.com/devyanigoil" target="_blank" rel="noopener noreferrer">
        <button className="load-more-btn">
          <FaSync className="refresh-icon" /> LOAD MORE
        </button>
        </a>
      </div>
    </div>
  );
};

export default ProjectsPage;
