import Sidebar from './components/Sidebar';
import HomePage from './components/HomePage';
import './App.css';
import AboutPage from './components/AboutPage';
import InterestsPage from './components/InterestsPage';
import SkillsPage from './components/SkillsPage';
import EducationPage from './components/EducationPage';
import WorkExperiencePage from './components/WorkExperiencePage';
import POR from './components/POR';
import Certifications from './components/Certifications';
import ContactPage from './components/ContactPage';
import ProjectsPage from './components/ProjectsPage';
// import BgAnimation from './components/BgAnimation';
import React, { useCallback } from "react";
import Particles from "react-particles";
import { loadFull } from "tsparticles";

function App() {
  const options = {
    fullScreen: {
      enable: false,
    },
    style: {
      position: "absolute",
      width: "100%",
      height: "100%",
    },
    particles: {
      number: {
        value: 80,
        density: {
          enable: true,
          area: 1000
        }
      },
      color: {
        value: ["#2EB67D", "#ECB22E", "#E01E5B", "#36C5F0"]
      },
      shape: {
        type: "circle"
      },
      opacity: {
        value: 1
      },
      size: {
        value: { min: 1, max: 8 }
      },
      links: {
        enable: true,
        distance: 150,
        color: "#808080",
        opacity: 0.4,
        width: 1
      },
      move: {
        enable: true,
        speed: 2,
        direction: "none",
        random: false,
        straight: false,
        outModes: "out"
      }
    },
    interactivity: {
      events: {
        onHover: {
          enable: true,
          mode: "grab"
        },
        onClick: {
          enable: true,
          mode: "push"
        }
      },
      modes: {
        grab: {
          distance: 140,
          links: {
            opacity: 1
          }
        },
        push: {
          quantity: 4
        }
      }
    }
  };

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  return (
      <div className="App">
        <div className="bg-animation"> 
          <Particles options={options} init={particlesInit}/> 
        </div>
        <Sidebar />
        <div className="content">
          <div id="home" className="section">
            <HomePage />
          </div>
          <div id="about" className="section">
            <AboutPage />
          </div>
          <div id="interests" className="section">
            <InterestsPage />
          </div>
          <div id="skills" className="section">
            <SkillsPage />
          </div>
          <div id="education" className="section">
            <EducationPage />
          </div>
          <div id="experience" className="section-left">
            <WorkExperiencePage />
          </div>
          <div id="por" className="section-left">
            <POR />
          </div>
          <div id="certifications" className="section">
            <Certifications />
          </div>
          <div id="projects" className="section">
            <ProjectsPage />
          </div>
          <div id="contact" className="section-left">
            <ContactPage />
          </div>
        </div>
    </div>
  );
}

export default App;
