import React from "react";
import './AboutPage.css'; // Import the CSS file for styling

const AboutPage = () => {
  return (
    <div className="about-page">
      <h1>WHO AM I?</h1>
      <p>
      <strong> Hi, I am Devyani Goil </strong>, an inquisitive computer science enthusiast pursuing a 
      Master of Science in Computer Science at the <strong> University of Massachusetts, Amherst </strong>. 
      With experience spanning cloud technologies, backend development, and scalable system design, 
      I thrive at the intersection of creativity and technology.
      </p>
      <p>
      I am a highly adaptive and flexible individual actively looking for internship opportunity in 
      Software Development where I can leverage my experience, technical expertise, and problem- solving 
      skills and build applications specific to the business needs of the company and help improve the 
      lives of those around me.
      My experience with hackathons and academic projects has nurtured my ability to adapt, innovate, 
      and communicate effectively, ensuring cohesive teamwork. With a focus on continuous improvement, 
      I bring fresh perspectives to complex challenges, ensuring my contributions are both impactful 
      and sustainable.
      </p>
    </div>
  );
};

export default AboutPage;
