import React from "react";
import { FaSync } from "react-icons/fa";
import './ProjectsPage.css';

const ProjectsPage = () => {
  const projects = [
    {
      title: "UMate - Bumble for Roommates",
      description: "Users can easily create a profile, set their preferences, and find potential roommates using a swipe interfaceusing React, Node.js, and MongoDB.",
      img: "${process.env.PUBLIC_URL}/UMateLogo.jpeg",
      link: "https://github.com/devyanigoil/UMate",
    },
    {
      title: "Project 2",
      description: "Project 2 is a machine learning project where we use Python and Scikit-Learn to predict house prices based on certain features.",
      img: "https://via.placeholder.com/300x200/32CD32/FFFFFF?text=Project+2",
    },
    {
      title: "Project 3",
      description: "Project 3 is a mobile app that helps track daily fitness activities. Built with React Native and integrated with health data APIs.",
      img: "https://via.placeholder.com/300x200/FFD700/FFFFFF?text=Project+3",
    },
    {
      title: "Project 4",
      description: "A real-time chat application developed with Node.js, Express, and Socket.io that allows users to chat instantly.",
      img: "https://via.placeholder.com/300x200/8A2BE2/FFFFFF?text=Project+4",
    },
    {
      title: "Project 5",
      description: "A weather forecasting web app that provides real-time weather information for any city, built with React and OpenWeather API.",
      img: "https://via.placeholder.com/300x200/DC143C/FFFFFF?text=Project+5",
    },
    {
      title: "Project 6",
      description: "Project 6 is an e-commerce website built with React and Redux for state management, featuring a full shopping cart experience.",
      img: "https://via.placeholder.com/300x200/20B2AA/FFFFFF?text=Project+6",
    },
  ];

  return (
    <div className="projects-section">
      <h1>PROJECTS</h1>
      <div className="projects-container">
        {projects.map((project, index) => (
          <div key={index} className="project-card" style={{ backgroundImage: `url(${project.img})` }}>
            <div className="overlay">
            <a href={project.link} target="_blank" rel="noopener noreferrer">
              <h2>{project.title}</h2> </a>
              <p>{project.description}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="load-more">
        <a href="https://github.com/devyanigoil" target="_blank" rel="noopener noreferrer">
        <button className="load-more-btn">
          <FaSync className="refresh-icon" /> LOAD MORE
        </button>
        </a>
      </div>
    </div>
  );
};

export default ProjectsPage;
